<?php/** * Accordion lens file * * This file produces a collapsible widget out of queried posts * Please note, for this widget to work, Twiiter Bootstrap's stylesheet and script file must already be in the page. * * @since 0.2 */static $collapsibles_instance;if( $posts->have_posts() ) {	$collapsibles_instance++; $i = 0;	?><div class="accordion" id="accordion-<?php echo $collapsibles_instance; ?>">		<?php while( $posts->have_posts() ) : $posts->the_post(); ?>			<div class="accordion-group">				<div class="accordion-heading">					<a class="accordion-toggle" data-toggle="collapse" data-parent="#accordion-<?php echo $collapsibles_instance; ?>" href="#collapse-<?php echo $collapsibles_instance . $i ?>"><?php the_title(); ?></a>				</div>				<div class="accordion-body collapse <?php echo $i == 0 ? 'in' : '' ?>" id="collapse-<?php echo $collapsibles_instance . $i ?>">					<div class="accordion-inner">						<?php the_content(); ?>					</div>				</div>			</div>		<?php $i++; endwhile; ?>	</div><?php}wp_reset_postdata();