<?php/** * Carousel lens file * * This file produces a collapsible Carousel out of queried posts * Please note, for this widget to work, Twiiter Bootstrap's stylesheet and script file must already be in the page. * * @since 0.2 */static $carousels_instance;if( $posts->have_posts() ) {	$carousels_instance++; $i = 0;	?><div class="carousel slide" id="carousel-<?php echo $carousel_instance; ?>">		<ol class="carousel-indicators">			<?php while( $i < $GLOBALS['wp_query']->post_count ) : ?>				<li data-target="#carousel-<?php echo $carousel_instance; ?>" data-slide-to="<?php echo $i; ?>" class="<?php echo ( $i == 0 ) ? 'active' : '' ?>"></li>			<?php $i++; endwhile; ?>		</ol>		<div class="carousel-inner">			<?php $i = 0; while( $posts->have_posts() ) : $posts->the_post(); ?>				<div class="<?php echo ( $i == 0 ) ? 'active' : ''; ?> item">					<?php the_post_thumbnail( 'large' ); ?>					<div class="carousel-caption">						<h4><?php the_title(); ?></h4>						<?php echo ( $content_limit ) ? wp_trim_words( get_the_content(), $content_limit ) : get_the_content(); ?>					</div>				</div>			<?php $i++; endwhile; ?>		</div>		<a class="carousel-control left" href="#carousel-<?php echo $carousel_instance; ?>" data-slide="prev">&lsaquo;</a>		<a class="carousel-control right" href="#carousel-<?php echo $carousel_instance; ?>" data-slide="next">&rsaquo;</a>	</div><?php}wp_reset_postdata();